import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import "./Layout.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  useEffect(() => {
    fetch("/events.json")
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error("Error loading events.json:", err));
  }, []);

  const handleEventClick = (clickInfo) => {
    setSelectedEvent(clickInfo.event.extendedProps);
  };

  const closePopup = () => setSelectedEvent(null);

  return (
    <div className="outer-card">
      <h2>Kommande Evenemang</h2>
      <div className="inner-card">
        <div className="inner-card-content">
          <FullCalendar
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="dayGridMonth"
            headerToolbar={{
              left: "prev,next today",
              center: "title",
              right: "dayGridMonth,timeGridWeek,timeGridDay",
            }}
            events={events.map((e) => ({
              title: e.name,
              start: e.date + "T" + e.time,
              extendedProps: {
                name: e.name,
                date: e.date,
                time: e.time,
                description: e.description,
              },
            }))}
            eventClick={handleEventClick}
          />
        </div>
      </div>

      {selectedEvent && (
        <div className="lightbox" onClick={closePopup}>
          <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
            <h3>{selectedEvent.name}</h3>
            <p>
              <strong>Datum:</strong> {selectedEvent.date} <br />
              <strong>Tid:</strong> {selectedEvent.time}
            </p>
            <p>{selectedEvent.description}</p>
            <button onClick={closePopup}>St√§ng</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Events;
