import React, { useEffect, useState } from "react";
import "../Layout.css"; // återanvänd Layout.css

function ClubRules() {
  const [howToJoin, setHowToJoin] = useState([]);
  const [clubRules, setClubRules] = useState([]);
  const [membershipBenefits, setMembershipBenefits] = useState([]);

  // Hämta JSON-data
  useEffect(() => {
    const fetchJson = async (path, setState) => {
      try {
        const res = await fetch(path);
        if (!res.ok) throw new Error(`Kunde inte hämta ${path}`);
        const data = await res.json();
        setState(data.paragraphs || []);
      } catch (err) {
        console.error(err);
      }
    };

    fetchJson("/data/howToJoinText.json", setHowToJoin);
    fetchJson("/data/clubRulesText.json", setClubRules);
    fetchJson("/data/membershipBenefitsText.json", setMembershipBenefits);
  }, []);

  return (
    <div className="rules-container">
      <div className="outer-card">
        <h1>Bli Medlem</h1>
        <div className="inner-card">
          <ul>
            <h2>Hur blir jag medlem?</h2>
            {howToJoin.map((rule, index) => (
              <li
                key={index}
                style={{
                  fontWeight: 600,
                  wordWrap: "break-word",
                  overflowWrap: "break-word",
                  marginBottom: "8px",
                }}
              >
                {rule}
              </li>
            ))}
          </ul>

          <ul>
            <h2 style={{ marginTop: "20px" }}>Klubbregler</h2>
            {clubRules.map((rule, index) => (
              <li
                key={index}
                style={{
                  fontWeight: 600,
                  wordWrap: "break-word",
                  overflowWrap: "break-word",
                  marginBottom: "8px",
                }}
              >
                {rule}
              </li>
            ))}
          </ul>

          <ul>
            <h2 style={{ marginTop: "20px" }}>Vad ingår i medlemskapet?</h2>
            {membershipBenefits.map((benefit, index) => (
              <li
                key={index}
                style={{
                  fontWeight: 600,
                  wordWrap: "break-word",
                  overflowWrap: "break-word",
                  marginBottom: "8px",
                }}
              >
                {benefit}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default ClubRules;
