import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import logo from "../assets/logo.png";
import "../Layout.css";

function Navbar() {
  const [menuActive, setMenuActive] = useState(false);
  const [dropdowns, setDropdowns] = useState({ regler: false, kontakt: false });

  const toggleMenu = () => setMenuActive(!menuActive);

  const toggleDropdown = (name) => {
    setDropdowns((prev) => ({ ...prev, [name]: !prev[name] }));
  };

  const closeDropdowns = () => setDropdowns({ regler: false, kontakt: false });

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src={logo} alt="Kumla Sportfiske" className="logo" />
      </div>

      {/* Hamburger button */}
      <button className="hamburger" onClick={toggleMenu}>
        ☰
      </button>

      <div className={`navbar-right ${menuActive ? "active" : ""}`}>
        {/* Startsida */}
        <NavLink to="/" className="navlink" onClick={() => setMenuActive(false)}>Startsida</NavLink>

        {/* Regler Dropdown */}
        <div className="dropdown">
          <span
            className="navlink"
            onClick={() => toggleDropdown("regler")}
          >
            Regler ▼
          </span>
          <div
            className="dropdown-content"
            style={{ display: dropdowns.regler ? "block" : undefined }}
          >
            <NavLink to="/rules?lake=Lugnet" onClick={closeDropdowns}>Lugnet</NavLink>
            <NavLink to="/rules?lake=Säbylundssjön" onClick={closeDropdowns}>Säbylundssjön</NavLink>
            <NavLink to="/rules?lake=Nolsjön" onClick={closeDropdowns}>Nolsjön</NavLink>
            <hr style={{ margin: "5px 0", borderColor: "#0077cc" }} />
            <NavLink to="/clubrules" onClick={closeDropdowns}>Klubbregler</NavLink>
          </div>
        </div>

        {/* Kontakt Dropdown */}
        <div className="dropdown">
          <span
            className="navlink"
            onClick={() => toggleDropdown("kontakt")}
          >
            Kontakt ▼
          </span>
          <div
            className="dropdown-content"
            style={{ display: dropdowns.kontakt ? "block" : undefined }}
          >
            <NavLink to="/contact" onClick={closeDropdowns}>Allmän kontakt</NavLink>
            <NavLink to="/inspectors" onClick={closeDropdowns}>Tillsynsmän</NavLink>
            <NavLink to="/about" onClick={closeDropdowns}>Om oss</NavLink>
          </div>
        </div>

        {/* Övriga länkar */}
        <NavLink to="/news" className="navlink" onClick={() => setMenuActive(false)}>Nyheter</NavLink>
        <NavLink to="/gallery" className="navlink" onClick={() => setMenuActive(false)}>Galleri</NavLink>
        <a
          href="https://www.ifiske.se/fiske-sabylundssjon.htm"
          target="_blank"
          rel="noopener noreferrer"
          className="navlink"
          onClick={() => setMenuActive(false)}
        >
          iFiske
        </a>
      </div>
    </nav>
  );
}

export default Navbar;
