import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "../Layout.css";

// Importera JSON för varje sjö
import LugnetText from "../data/LugnetText.json";
import SäbylundssjönText from "../data/SäbylundssjönText.json";
import NolsjönText from "../data/NolsjönText.json";

const lakes = [
  { key: "Lugnet", name: "Lugnet", text: LugnetText },
  { key: "Säbylundssjön", name: "Säbylundssjön", text: SäbylundssjönText },
  { key: "Nolsjön", name: "Nolsjön", text: NolsjönText }
];

function Rules() {
  const location = useLocation();
  const [selectedLake, setSelectedLake] = useState(null);
  const [lakeText, setLakeText] = useState(null);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 1024);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const lakeKey = params.get("lake");
    const lake = lakes.find(l => l.key === lakeKey);
    if (lake) {
      setSelectedLake(lake);
      setLakeText(lake.text);
    }
  }, [location]);

  const renderText = () => {
    if (!lakeText) return <p>Laddar...</p>;
    return (
      <>
        <h2>{lakeText.title}</h2>
        <ul>
          {lakeText.paragraphs.map((p, i) => (
            <li key={i} style={{ fontWeight: 600 }}>{p}</li>
          ))}
        </ul>
        <p style={{ fontWeight: 700 }}>Adress: {lakeText.address}</p>
      </>
    );
  };

  return (
    <div className="rules-container">
      <div className="outer-card">
        <h1>{selectedLake ? selectedLake.name : "Välj sjö"}</h1>

        {/* Mobil */}
        {isMobile && selectedLake && renderText()}

        {/* Desktop */}
        {!isMobile && selectedLake && (
          <div className="inner-card">{renderText()}</div>
        )}
      </div>
    </div>
  );
}

export default Rules;
