import React, { useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

import "../Layout.css";

export default function Events() {
  // Exempel på events (admin kan lägga till här)
  const [events] = useState([
    {
      title: "Test Event",
      start: "2025-11-01T10:00:00",
      end: "2025-11-01T12:00:00",
      description: "Detta är ett test-event med fullständig information."
    },
    {
      title: "Möte med styrelsen",
      start: "2025-11-05T18:00:00",
      end: "2025-11-05T20:00:00",
      description: "Vi diskuterar planerade aktiviteter och budget."
    }
  ]);

  const [selectedEvent, setSelectedEvent] = useState(null);

  // När man klickar på ett event
  const handleEventClick = (clickInfo) => {
    setSelectedEvent({
      title: clickInfo.event.title,
      start: clickInfo.event.start,
      end: clickInfo.event.end,
      description: clickInfo.event.extendedProps.description,
    });
  };

  // Stänger modal
  const closeModal = () => setSelectedEvent(null);

  return (
    <div className="outer-card">
      <div className="inner-card">
        <div className="inner-card-content">
          <div className="calendar-container">
            <FullCalendar
              plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
              initialView="dayGridMonth"
              events={events}
              eventClick={handleEventClick}
              headerToolbar={{
                left: "prev,next today",
                center: "title",
                right: "dayGridMonth,timeGridWeek,timeGridDay"
              }}
              height="auto"
            />
          </div>
        </div>
      </div>

      {/* --- EVENT MODAL --- */}
      {selectedEvent && (
        <div className="event-modal active" onClick={closeModal}>
          <div className="event-modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="event-modal-close" onClick={closeModal}>&times;</span>
            <h2>{selectedEvent.title}</h2>
            <p><strong>Datum:</strong> {selectedEvent.start.toLocaleDateString()}</p>
            <p>
              <strong>Tid:</strong> {selectedEvent.start.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})} 
              - {selectedEvent.end.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
            </p>
            <p>{selectedEvent.description}</p>
          </div>
        </div>
      )}
    </div>
  );
}
