import React, { useState, useEffect, useRef } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import "../Layout.css";

export default function Events() {
  const [events, setEvents] = useState([]);
  const titleRef = useRef();
  const dateRef = useRef();

  // Läs in events från localStorage när sidan laddas
  useEffect(() => {
    const storedEvents = JSON.parse(localStorage.getItem("events")) || [];
    setEvents(storedEvents);
  }, []);

  // Spara events i localStorage vid ändring
  useEffect(() => {
    localStorage.setItem("events", JSON.stringify(events));
  }, [events]);

  const handleAddEvent = (e) => {
    e.preventDefault();
    const title = titleRef.current.value.trim();
    const date = dateRef.current.value;

    if (!title || !date) return alert("Fyll i både titel och datum.");

    const newEvent = {
      title,
      date,
      id: Date.now(),
    };

    setEvents([...events, newEvent]);
    titleRef.current.value = "";
    dateRef.current.value = "";
  };

  const handleEventClick = (clickInfo) => {
    if (
      window.confirm(`Vill du ta bort eventet "${clickInfo.event.title}"?`)
    ) {
      setEvents(events.filter((event) => event.id !== +clickInfo.event.id));
    }
  };

  return (
    <div className="card events-container">
      <h1>Evenemang</h1>
      <p>
        Här kan du lägga till, visa och ta bort evenemang för klubben. Datan
        sparas lokalt i din webbläsare.
      </p>

      <form onSubmit={handleAddEvent} className="event-form">
        <input
          type="text"
          placeholder="Titel på evenemang"
          ref={titleRef}
          className="event-input"
        />
        <input type="date" ref={dateRef} className="event-input" />
        <button type="submit" className="event-btn">
          Lägg till
        </button>
      </form>

      <div className="calendar-wrapper">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          locale="sv"
          events={events}
          eventClick={handleEventClick}
          height="auto"
        />
      </div>
    </div>
  );
}
