// Events.jsx
import React, { useState } from "react";
import { Calendar } from "@fullcalendar/core";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import "./Layout.css";

const Events = () => {
  const [events, setEvents] = useState([
    { title: "Test Event 1", start: "2025-11-05T10:00:00", end: "2025-11-05T12:00:00" },
    { title: "Test Event 2", start: "2025-11-08T14:00:00", end: "2025-11-08T15:30:00" },
  ]);

  const handleDateClick = (arg) => {
    const title = prompt("Eventets namn:");
    const time = prompt("Starttid (HH:MM):");
    if (title && time) {
      const startDate = new Date(arg.date);
      const [hours, minutes] = time.split(":");
      startDate.setHours(hours, minutes);

      const endDate = new Date(startDate);
      endDate.setHours(endDate.getHours() + 1); // standard 1 timme långt event

      setEvents([
        ...events,
        {
          title,
          start: startDate.toISOString(),
          end: endDate.toISOString(),
        },
      ]);
    }
  };

  return (
    <div className="events-container">
      <h1>Evenemang</h1>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        editable={true}
        selectable={true}
        events={events}
        dateClick={handleDateClick}
        eventTimeFormat={{
          hour: "2-digit",
          minute: "2-digit",
          hour12: false,
        }}
      />
    </div>
  );
};

export default Events;
