import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import logo from "../assets/logo.png";
import "../Layout.css";

function Navbar() {
  const [mobileMenuActive, setMobileMenuActive] = useState(false);
  const [openDropdown, setOpenDropdown] = useState(null); // null, 'rules', 'contact'

  const toggleMobileMenu = () => {
    setMobileMenuActive(!mobileMenuActive);
    setOpenDropdown(null); // stäng alla dropdowns när mobilen togglas
  };

  const toggleDropdown = (menu) => {
    if (openDropdown === menu) {
      setOpenDropdown(null);
    } else {
      setOpenDropdown(menu);
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src={logo} alt="Kumla Sportfiske" className="logo" />
      </div>

      <button className="hamburger" onClick={toggleMobileMenu}>
        ☰
      </button>

      <div className={`navbar-right ${mobileMenuActive ? "active" : ""}`}>
        <NavLink to="/" className="navlink" onClick={() => setMobileMenuActive(false)}>
          Startsida
        </NavLink>

        {/* Regler Dropdown */}
        <div className="dropdown">
          <span
            className="navlink"
            onClick={() => toggleDropdown("rules")}
          >
            Regler ▼
          </span>
          <div
            className="dropdown-content"
            style={{ display: openDropdown === "rules" || window.innerWidth > 768 ? "block" : "none" }}
          >
            <NavLink to="/rules?lake=Lugnet" onClick={() => setMobileMenuActive(false)}>Lugnet</NavLink>
            <NavLink to="/rules?lake=Säbylundssjön" onClick={() => setMobileMenuActive(false)}>Säbylundssjön</NavLink>
            <NavLink to="/rules?lake=Nolsjön" onClick={() => setMobileMenuActive(false)}>Nolsjön</NavLink>
            <hr style={{ margin: "5px 0", borderColor: "#0077cc" }} />
            <NavLink to="/clubrules" onClick={() => setMobileMenuActive(false)}>Klubbregler</NavLink>
          </div>
        </div>

        {/* Kontakt Dropdown */}
        <div className="dropdown">
          <span
            className="navlink"
            onClick={() => toggleDropdown("contact")}
          >
            Kontakt ▼
          </span>
          <div
            className="dropdown-content"
            style={{ display: openDropdown === "contact" || window.innerWidth > 768 ? "block" : "none" }}
          >
            <NavLink to="/contact" onClick={() => setMobileMenuActive(false)}>Allmän kontakt</NavLink>
            <NavLink to="/inspectors" onClick={() => setMobileMenuActive(false)}>Tillsynsmän</NavLink>
            <NavLink to="/about" onClick={() => setMobileMenuActive(false)}>Om oss</NavLink>
          </div>
        </div>

        <NavLink to="/news" className="navlink" onClick={() => setMobileMenuActive(false)}>Nyheter</NavLink>
        <NavLink to="/gallery" className="navlink" onClick={() => setMobileMenuActive(false)}>Galleri</NavLink>
        <a
          href="https://www.ifiske.se/fiske-sabylundssjon.htm"
          target="_blank"
          rel="noopener noreferrer"
          className="navlink"
        >
          iFiske
        </a>
      </div>
    </nav>
  );
}

export default Navbar;
