import React, { useEffect, useState } from "react";
import "../Layout.css";
import homeText from "../data/homeText.json";

function Home() {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  // Lyssna på fönsterstorlek
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 1024);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Ladda Facebook SDK direkt
  useEffect(() => {
    const loadFacebookSDK = () => {
      if (window.FB) {
        window.FB.XFBML.parse();
        return;
      }
      window.fbAsyncInit = function () {
        window.FB.init({ xfbml: true, version: "v16.0" });
      };
      const scriptId = "facebook-jssdk";
      if (!document.getElementById(scriptId)) {
        const js = document.createElement("script");
        js.id = scriptId;
        js.src = "https://connect.facebook.net/sv_SE/sdk.js";
        document.body.appendChild(js);
      }
    };
    loadFacebookSDK();
  }, []);

  const renderText = () => (
    <div className="home-text">
      <h1>Kumla Sportfiskeförening</h1>
      <h2>{homeText.title}</h2>
      {homeText.paragraphs.map((p, i) => (
        <p key={i}>
          <strong>{p}</strong>
        </p>
      ))}
    </div>
  );

  return (
    <div className="rules-container">
      <div className="outer-card">
        {isMobile ? (
          <>
            {renderText()}
            <div className="mobile-facebook facebook-card">
              <div
                className="fb-page"
                data-href="https://www.facebook.com/KumlaSportfiske/"
                data-tabs="timeline"
                data-adapt-container-width="true"
                data-hide-cover="false"
                data-show-facepile="true"
              ></div>
            </div>
          </>
        ) : (
          <div className="inner-card inner-card-desktop">
            <div className="inner-card-content">
              {renderText()}
              <div className="desktop-facebook facebook-card">
                <div
                  className="fb-page"
                  data-href="https://www.facebook.com/KumlaSportfiske/"
                  data-tabs="timeline"
                  data-width="340"
                  data-hide-cover="false"
                  data-show-facepile="true"
                ></div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
