import React, { useEffect, useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import "./Layout.css"; // Se till att du har Layout.css
import eventsData from "../assets/events.json"; // Placera din events.json här

const Events = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  useEffect(() => {
    // Ladda events från JSON
    setEvents(eventsData);
  }, []);

  // Konvertera JSON till FullCalendar-format
  const calendarEvents = events.map((e) => ({
    id: e.id,
    title: e.name,
    start: `${e.date}T${e.startTime}`,
    end: `${e.date}T${e.endTime}`,
    extendedProps: {
      description: e.description,
    },
  }));

  const handleEventClick = (clickInfo) => {
    setSelectedEvent({
      name: clickInfo.event.title,
      date: clickInfo.event.start.toLocaleDateString(),
      startTime: clickInfo.event.start.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
      endTime: clickInfo.event.end.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
      description: clickInfo.event.extendedProps.description,
    });
  };

  const closePopup = () => setSelectedEvent(null);

  // Kommande evenemang (sorterade)
  const upcomingEvents = events
    .filter(e => new Date(e.date) >= new Date())
    .sort((a,b) => new Date(a.date) - new Date(b.date));

  return (
    <div className="outer-card">
      <h2>Kommande Evenemang</h2>
      <ul>
        {upcomingEvents.map(e => (
          <li key={e.id}>
            {e.name} – {e.date} {e.startTime}-{e.endTime}
          </li>
        ))}
      </ul>

      <div className="inner-card">
        <div className="calendar-container">
          <FullCalendar
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
            initialView="dayGridMonth"
            events={calendarEvents}
            eventClick={handleEventClick}
            headerToolbar={{
              left: "prev,next today",
              center: "title",
              right: "dayGridMonth,timeGridWeek,timeGridDay",
            }}
            height="auto"
          />
        </div>
      </div>

      {/* Popup för event */}
      {selectedEvent && (
        <div className="lightbox">
          <div className="lightbox-content">
            <h3>{selectedEvent.name}</h3>
            <p><strong>Datum:</strong> {selectedEvent.date}</p>
            <p><strong>Tid:</strong> {selectedEvent.startTime} - {selectedEvent.endTime}</p>
            <p>{selectedEvent.description}</p>
            <button onClick={closePopup}>Stäng</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Events;
