// src/pages/Events.jsx
import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import "../Layout.css"; // din Layout.css

const Events = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  // Hämta events från events.json
  useEffect(() => {
    fetch("/events.json")
      .then((res) => res.json())
      .then((data) => setEvents(data))
      .catch((err) => console.error("Error loading events:", err));
  }, []);

  // När ett event klickas
  const handleEventClick = (clickInfo) => {
    setSelectedEvent({
      title: clickInfo.event.title,
      start: clickInfo.event.start,
      end: clickInfo.event.end,
      description: clickInfo.event.extendedProps.description,
    });
  };

  // Stäng modal
  const closeModal = () => setSelectedEvent(null);

  return (
    <div className="events-container">
      <h1>Kommande Evenemang</h1>
      <div className="events-wrapper">
        <FullCalendar
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          headerToolbar={{
            left: "prev,next today",
            center: "title",
            right: "dayGridMonth,timeGridWeek,timeGridDay",
          }}
          events={events}
          eventClick={handleEventClick}
          height="auto"
        />
      </div>

      {/* Event Modal */}
      {selectedEvent && (
        <div className="event-modal active">
          <div className="event-modal-content">
            <span className="event-modal-close" onClick={closeModal}>
              &times;
            </span>
            <h2>{selectedEvent.title}</h2>
            <p>
              <strong>Datum:</strong>{" "}
              {selectedEvent.start.toLocaleDateString()}{" "}
              {selectedEvent.start.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
            </p>
            {selectedEvent.end && (
              <p>
                <strong>Slut:</strong>{" "}
                {selectedEvent.end.toLocaleDateString()}{" "}
                {selectedEvent.end.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
              </p>
            )}
            <p>{selectedEvent.description}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Events;
