import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const lakes = [
  { key: "Lugnet", 
    name: "Lugnet", 
    rules: [
    "I Lugnet är det flugfiske och Catch & Release som gäller och man använder en hullingslös enkelkrok.", 
    "Fisket får fortsätta så länge man återsätter fisken som är i bra kondition.",
    "Den person som står på fiskekortet har rätten att fiska i den sjö som fiskekortet gäller.", 
    "Endast ett fiskeredskap är tillåtet per fiskekort!",
    "Tillåtna fiskemetoder är endast flugfiske med hullinglös enkelkrok. Endast en upphängare är tillåtet och ryckfiske är förbjudet."], 
    address: "Kommer snart" },
  { key: "Säbylundssjön", name: "Säbylundssjön", rules: ["Max 2 fiskar per dag"], address: "Kvarnsjön, Kumla kommun" },
  { key: "sickels", name: "Sickelsjön", rules: ["Catch & release. Ingen nätfiske."], address: "Sickelsjön, Kumla kommun" }
];

<div style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
  <h2>{selectedLake.name}</h2>
  
  <p><strong>Regler:</strong></p>
  <ul>
    {selectedLake.rules.map((rule, index) => (
      <li key={index}>{rule}</li>
    ))}
  </ul>

  <p><strong>Adress:</strong> {selectedLake.address}</p>
</div>

function Rules() {
  const location = useLocation();
  const [selectedLake, setSelectedLake] = useState(null);

  useEffect(() => {
    const query = location.search.replace("?", "");
    const lake = lakes.find(l => l.key === query);
    if (lake) setSelectedLake(lake);
  }, [location]);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Regler per sjö</h1>

      {/* Visa knappar endast om ingen sjö är vald via dropdown */}
      {!selectedLake && (
        <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
          {lakes.map(lake => (
            <button key={lake.key} onClick={() => setSelectedLake(lake)} style={{ padding: "10px", cursor: "pointer" }}>
              {lake.name}
            </button>
          ))}
        </div>
      )}

      {/* Visa info för vald sjö */}
      {selectedLake && (
        <div style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
          <h2>{selectedLake.name}</h2>
          <p><strong>Regler:</strong> {selectedLake.rules}</p>
          <p><strong>Adress:</strong> {selectedLake.address}</p>
        </div>
      )}
    </div>
  );
}

export default Rules;
