import React, { useEffect, useState } from "react";
import "../Layout.css";
import homeText from "../data/homeText.json";

function Home() {
  const [text, setText] = useState("");

  useEffect(() => {
    setText(homeText.content);

    const loadFacebookSDK = () => {
      if (window.FB) {
        window.FB.XFBML.parse();
      } else {
        window.fbAsyncInit = function () {
          window.FB.init({ xfbml: true, version: "v16.0" });
          window.FB.XFBML.parse();
        };

        (function (d, s, id) {
          let js,
            fjs = d.getElementsByTagName(s)[0];
          if (d.getElementById(id)) return;
          js = d.createElement(s);
          js.id = id;
          js.src = "https://connect.facebook.net/sv_SE/sdk.js";
          fjs.parentNode.insertBefore(js, fjs);
        })(document, "script", "facebook-jssdk");
      }
    };

    loadFacebookSDK();

    // Säkerställ att widgeten laddas även vid navigering i SPA
    const timer = setTimeout(() => {
      if (window.FB) window.FB.XFBML.parse();
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="rules-container">
      <div className="outer-card">
        {/* Desktop text + FB i inner-card */}
        <div className="inner-card inner-card-content">
          <div className="home-text">
            <h2>Välkommen!</h2>
            <p dangerouslySetInnerHTML={{ __html: text }} />
          </div>

          <div className="desktop-facebook facebook-card">
            <div
              className="fb-page"
              data-href="https://www.facebook.com/KumlaSportfiske/"
              data-tabs="timeline"
              data-width="340"
              data-height=""
              data-small-header="false"
              data-adapt-container-width="true"
              data-hide-cover="false"
              data-show-facepile="true"
            ></div>
          </div>
        </div>

        {/* Mobilversion */}
        <div className="mobile-home">
          <div className="home-text">
            <h2>Välkommen!</h2>
            <p dangerouslySetInnerHTML={{ __html: text }} />
          </div>
          <div className="mobile-facebook facebook-card">
            <div
              className="fb-page"
              data-href="https://www.facebook.com/KumlaSportfiske/"
              data-tabs="timeline"
              data-height=""
              data-small-header="false"
              data-adapt-container-width="true"
              data-hide-cover="false"
              data-show-facepile="true"
            ></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
