import React, { useEffect, useState } from "react";
import "../Layout.css";
import homeText from "../data/homeText.json";

function Home() {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  // Lyssna på fönsterändringar
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 1024);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Facebook SDK
  useEffect(() => {
    if (!window.FB) {
      window.fbAsyncInit = function () {
        window.FB.init({ xfbml: true, version: "v16.0" });
      };
      ((d, s, id) => {
        let js,
          fjs = d.getElementsByTagName(s)[0];
        if (d.getElementById(id)) return;
        js = d.createElement(s);
        js.id = id;
        js.src = "https://connect.facebook.net/sv_SE/sdk.js";
        fjs.parentNode.insertBefore(js, fjs);
      })(document, "script", "facebook-jssdk");
    } else {
      window.FB.XFBML.parse();
    }
  }, []);

  const renderText = () => (
    <div className="home-text">
      <h1>Kumla Sportfiskeförening</h1>
      <h2>{homeText.title}</h2>
      {homeText.paragraphs.map((p, i) => (
        <p key={i}>
          <strong>{p}</strong>
        </p>
      ))}
    </div>
  );

  return (
    <div className="rules-container">
      <div className="outer-card">
        {/* MOBIL */}
        {isMobile && (
          <>
            {renderText()}
            <div className="mobile-facebook facebook-card">
              <div
                className="fb-page"
                data-href="https://www.facebook.com/KumlaSportfiske/"
                data-tabs="timeline"
                data-height=""
                data-small-header="false"
                data-adapt-container-width="true"
                data-hide-cover="false"
                data-show-facepile="true"
              ></div>
            </div>
          </>
        )}

        {/* DESKTOP */}
        {!isMobile && (
          <div className="inner-card">
            {renderText()}
            <div className="desktop-facebook facebook-card">
              <div
                className="fb-page"
                data-href="https://www.facebook.com/KumlaSportfiske/"
                data-tabs="timeline"
                data-width="340"
                data-height=""
                data-small-header="false"
                data-adapt-container-width="true"
                data-hide-cover="false"
                data-show-facepile="true"
              ></div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
