import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import eventsData from "../data/nyheter/events.json";
import "../Layout.css";

const Events = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 1024);

  useEffect(() => {
    setEvents(eventsData);
  }, []);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 1024);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleEventClick = (info) => {
    setSelectedEvent({
      title: info.event.title,
      start: info.event.start,
      end: info.event.end,
      description: info.event.extendedProps.description || "",
    });
  };

  const closePopup = () => setSelectedEvent(null);

  const formatDateTime = (date) => {
    const d = new Date(date);
    const hours = d.getHours().toString().padStart(2, "0");
    const minutes = d.getMinutes().toString().padStart(2, "0");
    return `${d.toLocaleDateString()} ${hours}:${minutes}`;
  };

  const renderCalendar = () => {
    // Dynamisk aspectRatio beroende på mobil
    const aspectRatio = isMobile ? 0.8 : 1.35;

    return (
      <div
        className="calendar-container"
        style={{
          width: "100%",
          padding: isMobile ? "8px" : "20px",
          borderRadius: isMobile ? "0" : "12px",
          boxShadow: isMobile ? "none" : "0 6px 26px rgba(0,0,0,0.06)",
          background: isMobile ? "transparent" : "var(--card-bg)",
        }}
      >
        <FullCalendar
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          events={events}
          eventClick={handleEventClick}
          headerToolbar={{
            left: "prev",
            center: "title",
            right: "next",
          }}
          eventTimeFormat={{ hour: "2-digit", minute: "2-digit", hour12: false }}
          height="auto"
          contentHeight="auto"
          aspectRatio={aspectRatio}
        />
      </div>
    );
  };

  return (
    <div className="rules-container">
      <div className="outer-card">
        <h1>Kommande Evenemang</h1>

        {/* Mobil / Desktop */}
        {isMobile ? renderCalendar() : <div className="inner-card">{renderCalendar()}</div>}

        {/* Lightbox */}
        {selectedEvent && (
          <div className="lightbox" onClick={closePopup}>
            <div
              className="lightbox-content"
              style={{ maxWidth: "500px", padding: "24px", borderRadius: "12px" }}
              onClick={(e) => e.stopPropagation()}
            >
              <h3>{selectedEvent.title}</h3>
              <p>
                <strong>Start:</strong> {formatDateTime(selectedEvent.start)}
              </p>
              {selectedEvent.end && (
                <p>
                  <strong>Slut:</strong> {formatDateTime(selectedEvent.end)}
                </p>
              )}
              {selectedEvent.description && <p>{selectedEvent.description}</p>}
              <button
                onClick={closePopup}
                style={{
                  marginTop: "12px",
                  padding: "8px 16px",
                  borderRadius: "6px",
                  backgroundColor: "var(--accent)",
                  border: "none",
                  color: "#fff",
                  cursor: "pointer",
                  fontWeight: 600,
                }}
              >
                Stäng
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Events;
