import React, { useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import "./Layout.css";

const Events = () => {
  const [events, setEvents] = useState([
    { title: "Fisketävling", start: "2025-11-10T09:00:00", end: "2025-11-10T15:00:00" },
    { title: "Medlemsmöte", start: "2025-11-12T18:00:00", end: "2025-11-12T20:00:00" },
    { title: "Barnfiske", start: "2025-11-15T10:00:00", end: "2025-11-15T14:00:00" }
  ]);

  // Hantera klick på kalender (t.ex. lägga till event)
  const handleDateClick = (info) => {
    const title = prompt("Eventnamn:");
    const time = prompt("Tid (HH:MM, t.ex. 14:00):", "10:00");
    if (title && time) {
      const startDate = new Date(info.dateStr + "T" + time + ":00");
      setEvents([...events, { title, start: startDate }]);
    }
  };

  return (
    <div className="events-container">
      <h1>Evenemangskalender</h1>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          left: "prev,next today",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay"
        }}
        events={events}
        dateClick={handleDateClick}
        eventTimeFormat={{
          hour: '2-digit',
          minute: '2-digit',
          hour12: false
        }}
        height="auto"
      />
    </div>
  );
};

export default Events;
