import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import logo from "../assets/logo.png";
import "../Layout.css";

function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [rulesOpen, setRulesOpen] = useState(false);
  const [contactOpen, setContactOpen] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  // Uppdatera isMobile vid fönsterstorlek ändring
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleRules = () => isMobile && setRulesOpen(!rulesOpen);
  const toggleContact = () => isMobile && setContactOpen(!contactOpen);

  const closeAll = () => {
    setMenuOpen(false);
    setRulesOpen(false);
    setContactOpen(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img src={logo} alt="Kumla Sportfiske" className="logo" />
      </div>

      <button className="hamburger" onClick={toggleMenu}>
        ☰
      </button>

      <div className={`navbar-right ${menuOpen ? "active" : ""}`}>
        <NavLink to="/" className="navlink" onClick={closeAll}>Startsida</NavLink>

        {/* Regler Dropdown */}
        <div className="dropdown">
          <span className="navlink" onClick={toggleRules}>Regler ▼</span>
          <div className="dropdown-content" style={{ display: !isMobile || rulesOpen ? "block" : "none" }}>
            <NavLink to="/rules?lake=Lugnet" onClick={closeAll}>Lugnet</NavLink>
            <NavLink to="/rules?lake=Säbylundssjön" onClick={closeAll}>Säbylundssjön</NavLink>
            <NavLink to="/rules?lake=Nolsjön" onClick={closeAll}>Nolsjön</NavLink>
            <hr style={{ margin: "5px 0", borderColor: "#0077cc" }} />
            <NavLink to="/clubrules" onClick={closeAll}>Klubbregler</NavLink>
          </div>
        </div>

        {/* Kontakt Dropdown */}
        <div className="dropdown">
          <span className="navlink" onClick={toggleContact}>Kontakt ▼</span>
          <div className="dropdown-content" style={{ display: !isMobile || contactOpen ? "block" : "none" }}>
            <NavLink to="/contact" onClick={closeAll}>Allmän kontakt</NavLink>
            <NavLink to="/inspectors" onClick={closeAll}>Tillsynsmän</NavLink>
            <NavLink to="/about" onClick={closeAll}>Om oss</NavLink>
          </div>
        </div>

        {/* Övriga länkar */}
        <NavLink to="/news" className="navlink" onClick={closeAll}>Nyheter</NavLink>
        <NavLink to="/gallery" className="navlink" onClick={closeAll}>Galleri</NavLink>
        <a
          href="https://www.ifiske.se/fiske-sabylundssjon.htm"
          target="_blank"
          rel="noopener noreferrer"
          className="navlink"
          onClick={closeAll}
        >
          iFiske
        </a>
      </div>
    </nav>
  );
}

export default Navbar;
