import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./Rules.css";

const inspectors = [
  { name: "John Doe", lake: "Lugnet", phone: "070-123 45 67" },
  { name: "Jane Doe", lake: "S√§bylundssj√∂n", phone: "070-987 65 43" },
  { name: "Abed Balhawan", lake: "Nolsj√∂n", phone: "070-xxx xx xx" }
];

function Contact() {
  const location = useLocation();
  const [activeTab, setActiveTab] = useState("general"); // "general", "inspectors", "about"

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const type = params.get("type");
    if (type) setActiveTab(type);
  }, [location]);

  return (
    <div style={{ padding: "20px" }}>
      <h1>
        {activeTab === "general" && "Kontakt"}
        {activeTab === "inspectors" && "Tillsynsm√§n"}
        {activeTab === "about" && "Om oss"}
      </h1>

      <div className="rules-container text-break">
        {activeTab === "general" && (
          <>
            <h2>Allm√§n kontakt</h2>
            <form>
              <input
                type="text"
                placeholder="Ditt namn"
                style={{ display: "block", marginBottom: "10px", padding: "8px", width: "100%" }}
              />
              <input
                type="email"
                placeholder="Din e-post"
                style={{ display: "block", marginBottom: "10px", padding: "8px", width: "100%" }}
              />
              <textarea
                placeholder="Meddelande"
                style={{ display: "block", marginBottom: "10px", padding: "8px", width: "100%", minHeight: "100px" }}
              />
              <button type="submit" style={{ padding: "10px 20px", cursor: "pointer" }}>Skicka</button>
            </form>
          </>
        )}

        {activeTab === "inspectors" && (
          <>
            <h2>Tillsynsm√§n</h2>
            <ul style={{ listStyleType: "none", padding: 0 }}>
              {inspectors.map(ins => (
                <li
                  key={ins.name}
                  style={{
                    backgroundColor: "#f0f8ff",
                    marginBottom: "10px",
                    padding: "10px",
                    borderRadius: "6px",
                    border: "1px solid #ccc"
                  }}
                >
                  <strong>{ins.name}</strong> ‚Äì {ins.lake} ‚Äì üìû {ins.phone}
                </li>
              ))}
            </ul>
          </>
        )}

        {activeTab === "about" && (
          <>
            <h2>Om oss</h2>
            <p>
              Kumla Sportfiskef√∂rening √§r en ideell klubb som arbetar f√∂r ett h√•llbart och trevligt fiske i v√•ra sj√∂ar. 
              Vi ordnar aktiviteter f√∂r medlemmar, sk√∂ter v√•ra vatten och vill skapa en s√§ker och rolig milj√∂ f√∂r b√•de barn och vuxna.
              Kontakta oss g√§rna f√∂r mer information om medlemskap eller klubbens verksamhet.
            </p>
          </>
        )}
      </div>
    </div>
  );
}

export default Contact;
