import React, { useState } from "react";
import "../Layout.css";

// Importera alla bilder fr책n assets/gallery
const importAll = (r) => r.keys().map(r);
const images = importAll(require.context("../assets/gallery", false, /\.(png|jpe?g|svg)$/));

function Gallery() {
  const [lightboxIndex, setLightboxIndex] = useState(null);

  const openLightbox = (index) => setLightboxIndex(index);
  const closeLightbox = () => setLightboxIndex(null);

  const nextImage = () => setLightboxIndex((lightboxIndex + 1) % images.length);
  const prevImage = () =>
    setLightboxIndex((lightboxIndex - 1 + images.length) % images.length);

  return (
    <div className="gallery-container">
      {/* Yttre kort med rubrik */}
      <div className="outer-card">
        <h1>Galleri</h1>
        <h2>Bilder fr책n v책ra medlemmar</h2>

        {/* Inre kort med bilder */}
        <div className="inner-card">
          <div className="gallery-grid">
            {images.map((img, index) => (
              <div
                key={index}
                className="gallery-item"
                onClick={() => openLightbox(index)}
              >
                <img
                  src={img}
                  alt={`Galleri ${index + 1}`}
                  loading="lazy"
                />
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Lightbox / Modal */}
      {lightboxIndex !== null && (
        <div className="lightbox" onClick={closeLightbox}>
          <span className="close" onClick={closeLightbox}>&times;</span>
          <img
            className="lightbox-content"
            src={images[lightboxIndex]}
            alt={`Galleri ${lightboxIndex + 1}`}
          />
          <button className="prev" onClick={(e) => { e.stopPropagation(); prevImage(); }}>&#10094;</button>
          <button className="next" onClick={(e) => { e.stopPropagation(); nextImage(); }}>&#10095;</button>
        </div>
      )}
    </div>
  );
}

export default Gallery;
